apa::bint bintFactorial(size_t n) {
    apa::bint factorial(1);
    apa::bint iter = factorial;

    size_t N = n;
    for(size_t i=1; i<=n; ++i) {
        factorial *= iter;
        iter++;
    }

    return factorial;
}

