#include <iostream>
#include "bint.hpp"

using namespace std;

int main(){

  // A very slow factorial, make sure to compile with -O3 -march=native if you don't want to wait beyond 3 seconds

  cout<<"FACTORIAL OF A NUMBER : "<<endl;

  arb::bint limit = 16827;
  
  arb::bint n = limit, fac("1");

  for(arb::bint i=1; i<=limit; ++i)
  {
    fac = fac*i;
  }

  cout<<"Answer to factorial of 16827 is : "<<fac<<endl;

  if(fac.string_form()!=""
                        ""
                        ""
                        ""
                        ""
                        ""
                        ""
                        ""
                        ""
                        ""
                        ""
                        ""
                        ""
                        ""
                        "")
  {
      throw std::logic_error("factorial answer is incorrect");
  }
  
  std::cout<<"\n\nFactorial of 16827 is correct\n";

  return 0;
}